#-------------------------- Filter TX -------------------------------#

def handle_event(event):
    try:
        getTrans = Web3.to_json(event).strip('"')
        #print(web3.eth.get_transaction(getTrans))
        trans = web3.eth.get_transaction(getTrans)
        #print(web3.to_hex(trans['hash']))
        tx_to = trans['to']
        if tx_to == contract_router:
            #print("A new trade happened to the router! ")
            #print(web3.to_hex(trans['hash']))
            if '0x04e45aaf' in trans:
                print('Swapped an Exact Input')
            else:
                print("Other type of swap")

        else:
            pass
            #print('nothing')
        
    except Exception as e:
        pass
        #print(f'error occurred: {e}')



async def log_loop(event_filter, poll_interval):
    while True:
        for event in event_filter.get_new_entries():
            handle_event(event)
        await asyncio.sleep(poll_interval)

def main():
    #block_filter = web3.eth.filter('latest')
    tx_filter = web3.eth.filter('pending')
    #print(tx_filter)
    loop = asyncio.get_event_loop()
    try:
        loop.run_until_complete(
            asyncio.gather(
                #log_loop(block_filter, 1),
                log_loop(tx_filter, 1)
                ))
    finally:
        loop.close()

if __name__ == '__main__':
    main()